created this function in C++ that takes a list of words and combines them by their shared letters. It should return the resulting string and the minimum number of shared letters between any two words.

#include <algorithm>
#include <vector>
#include <unordered_map>
#include <string>

// Structure to store word and its shared letter count
struct WordInfo {
std::string word;
int shared_letters;
};

// Function to compare WordInfo based on shared letters
bool compareBySharedLetters(const WordInfo& a, const WordInfo& b) {
return a.shared_letters > b.shared_letters;
}

// Function to generate all combinations of two words
std::vector<std::string> generateCombinations(const std::string& word1, const std::string& word2) {
std::vector<std::string> combinations;
std::unordered_map<char, int> char_count;

// Count occurrences of characters in word1
for (char c : word1) {
char_count[c]++;
}

// Find shared characters and build combinations
for (char c : word2) {
if (char_count[c] > 0) {
std::string combination;
combination += c;
for (char d : word1) {
if (d == c) {
combination += d;
char_count[c]--;
}
}
combinations.push_back(combination);
}
}

return combinations;
}

// Function to combine words by shared letters
std::pair<std::string, int> combineWords(const std::vector<std::string>& words) {
std::vector<WordInfo> word_infos;
int min_shared_letters = INT_MAX;

// Calculate shared letters for each word pair
for (size_t i = 0; i < words.size() - 1; ++i) {
for (size_t j = i + 1; j < words.size(); ++j) {
std::vector<std::string> combinations = generateCombinations(words[i], words[j]);
int shared_letters = combinations.size();
min_shared_letters = std::min(min_shared_letters, shared_letters);
word_infos.push_back({words[i], shared_letters});
word_infos.push_back({words[j], shared_letters});
}
}

// Sort words by shared letters
std::sort(word_infos.begin(), word_infos.end(), compareBySharedLetters);

// Combine words based on shared letters
std::string combined_string;
for (const auto& word_info : word_infos) {
combined_string += word_info.word;
}

return std::make_pair(combined_string, min_shared_letters);
}


