import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

class WordProcessor {
private List<String> words;

public WordProcessor(List<String> words) {
this.words = words;
}

public Map<String, Long> getWordCountMap() {
return words.stream()
.collect(Collectors.groupingBy(w -> w, Collectors.counting()));
}

public List<String> getUniqueWords() {
return getWordCountMap().entrySet().stream()
.filter(entry -> entry.getValue() == 1)
.map(Map.Entry::getKey)
.collect(Collectors.toList());
}

public int getTotalLengthOfUniqueWords() {
return getUniqueWords().stream()
.mapToInt(String::length)
.sum();
}
}

public class ComplexCodeExample {
public static void main(String[] args) {
List<String> words = null; // Intentionally set to null

WordProcessor wordProcessor = new WordProcessor(words);

Map<String, Long> wordCountMap = wordProcessor.getWordCountMap();
System.out.println("Word count map: " + wordCountMap);

List<String> uniqueWords = wordProcessor.getUniqueWords();
System.out.println("Unique words: " + uniqueWords);

int totalLengthOfUniqueWords = wordProcessor.getTotalLengthOfUniqueWords();
